import { Component, OnInit, Inject, PLATFORM_ID } from '@angular/core';
import { CommonModule, isPlatformBrowser } from '@angular/common';
import { RouterModule, Router, NavigationEnd } from '@angular/router';
import { filter } from 'rxjs/operators';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <header [class.scrolled]="isScrolled" [class.simple-header]="!isHomePage">
      <div class="container">
        <div class="logo-section" (click)="navigateToHome()">
          <img src="assets/logo.jpg" alt="Logo Groupe IGA" class="logo" />
          <div class="brand-text">
            <div class="brand-name">IGA</div>
            <div class="brand-tagline">L'eau en confiance</div>
          </div>
        </div>
        
        <!-- Navigation adaptative selon la page -->
        <div class="nav-wrapper" [class.simple-nav]="!isHomePage">
          <nav [class.mobile-open]="isMobileMenuOpen">
            <!-- Navigation pour la page d'accueil avec ancres -->
            <ng-container *ngIf="isHomePage">
              <a routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">Accueil</a>
              <a href="#expertises" (click)="scrollToSection('expertises', $event)" [class.active]="activeSection === 'expertises'">Nos expertises</a>
              <a href="#realisations" (click)="scrollToSection('realisations', $event)" [class.active]="activeSection === 'realisations'">Réalisations</a>
              <a href="#about" (click)="scrollToSection('about', $event)" [class.active]="activeSection === 'about'">À propos</a>
              <a href="#contact" (click)="scrollToSection('contact', $event)" [class.active]="activeSection === 'contact'">Contact</a>
            </ng-container>
            
            <!-- Navigation pour les autres pages avec routes -->
            <ng-container *ngIf="!isHomePage">
              <a routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">Accueil</a>
              <a routerLink="/services" routerLinkActive="active">Nos Services</a>
              <a routerLink="/realisations" routerLinkActive="active">Réalisations</a>
              <a routerLink="/about" routerLinkActive="active">À propos</a>
              <a routerLink="/contact" routerLinkActive="active">Contact</a>
            </ng-container>
          </nav>
          
          <!-- Bouton menu mobile -->
          <button class="mobile-menu-toggle" (click)="toggleMobileMenu()" [class.open]="isMobileMenuOpen">
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </div>
    </header>
  `,
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
  isScrolled = false;
  isHomePage = true;
  isMobileMenuOpen = false;
  activeSection = '';

  constructor(
    private router: Router,
    @Inject(PLATFORM_ID) private platformId: Object
  ) {}

  ngOnInit() {
    if (isPlatformBrowser(this.platformId)) {
      // Écouter les changements de route
      this.router.events.pipe(
        filter(event => event instanceof NavigationEnd)
      ).subscribe((event) => {
        const navigationEvent = event as NavigationEnd;
        this.isHomePage = navigationEvent.url === '/' || navigationEvent.url === '/home';
        this.closeMobileMenu();
      });

      // Initialiser l'état de la page
      this.isHomePage = this.router.url === '/' || this.router.url === '/home';

      // Écouter le scroll seulement si on est sur la page d'accueil
      if (this.isHomePage) {
        this.setupScrollListener();
        this.setupSectionObserver();
      } else {
        // Sur les autres pages, on considère toujours qu'on a scrollé
        this.isScrolled = true;
      }
    }
  }

  private setupScrollListener() {
    if (isPlatformBrowser(this.platformId)) {
      window.addEventListener('scroll', () => {
        this.isScrolled = window.scrollY > 50;
      });
    }
  }

  private setupSectionObserver() {
    if (isPlatformBrowser(this.platformId)) {
      const sections = ['expertises', 'realisations', 'about', 'contact'];
      const observer = new IntersectionObserver(
        (entries) => {
          entries.forEach(entry => {
            if (entry.isIntersecting) {
              this.activeSection = entry.target.id;
            }
          });
        },
        { threshold: 0.3 }
      );

      // Observer les sections après un délai pour s'assurer qu'elles sont rendues
      setTimeout(() => {
        sections.forEach(sectionId => {
          const element = document.getElementById(sectionId);
          if (element) {
            observer.observe(element);
          }
        });
      }, 1000);
    }
  }

  navigateToHome() {
    if (this.isHomePage) {
      // Si on est déjà sur la page d'accueil, scroll vers le haut
      if (isPlatformBrowser(this.platformId)) {
        window.scrollTo({ top: 0, behavior: 'smooth' });
      }
    } else {
      // Sinon, naviguer vers la page d'accueil
      this.router.navigate(['/']);
    }
    this.closeMobileMenu();
  }

  scrollToSection(sectionId: string, event: Event) {
    event.preventDefault();

    if (!this.isHomePage) {
      // Si on n'est pas sur la page d'accueil, naviguer d'abord vers la page d'accueil
      this.router.navigate(['/']).then(() => {
        setTimeout(() => this.scrollToElement(sectionId), 100);
      });
    } else {
      this.scrollToElement(sectionId);
    }

    this.closeMobileMenu();
  }

  private scrollToElement(sectionId: string) {
    if (isPlatformBrowser(this.platformId)) {
      const element = document.getElementById(sectionId);
      if (element) {
        const headerHeight = 80; // Hauteur approximative du header
        const elementPosition = element.offsetTop - headerHeight;

        window.scrollTo({
          top: elementPosition,
          behavior: 'smooth'
        });
      }
    }
  }

  toggleMobileMenu() {
    this.isMobileMenuOpen = !this.isMobileMenuOpen;
  }

  closeMobileMenu() {
    this.isMobileMenuOpen = false;
  }
}
