import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <div class="home-container">
      <section class="hero-section">
        <div class="hero-content">
          <h1>Bienvenue chez IGA</h1>
          <p class="hero-subtitle">Votre partenaire pour la gestion de l'eau et de l'environnement</p>
          <div class="cta-buttons">
            <a routerLink="/services" class="btn btn-primary">Nos Services</a>
            <a routerLink="/admin/login" class="btn btn-secondary">Administration</a>
          </div>
        </div>
        <div class="hero-animation">
          <div class="water-drop"></div>
        </div>
      </section>

      <section class="features-section">
        <div class="container">
          <h2>Nos Domaines d'Expertise</h2>
          <div class="features-grid">
            <div class="feature-card">
              <div class="feature-icon">💧</div>
              <h3>Gestion de l'Eau</h3>
              <p>Solutions innovantes pour la distribution et le traitement de l'eau</p>
            </div>
            <div class="feature-card">
              <div class="feature-icon">🌱</div>
              <h3>Environnement</h3>
              <p>Protection et préservation de notre écosystème</p>
            </div>
            <div class="feature-card">
              <div class="feature-icon">⚡</div>
              <h3>Efficacité Énergétique</h3>
              <p>Optimisation des ressources pour un avenir durable</p>
            </div>
          </div>
        </div>
      </section>
    </div>
  `,
  styles: [`
    .home-container {
      min-height: 100vh;
    }

    .hero-section {
      min-height: 100vh;
      background: linear-gradient(135deg, #0077C2 0%, #4CAF50 100%);
      display: flex;
      align-items: center;
      justify-content: space-between;
      padding: 2rem 5%;
      color: white;
    }

    .hero-content {
      flex: 1;
      max-width: 600px;
    }

    .hero-content h1 {
      font-size: 3.5rem;
      font-weight: 700;
      margin-bottom: 1rem;
      animation: fadeInUp 1s ease-out;
    }

    .hero-subtitle {
      font-size: 1.2rem;
      margin-bottom: 2rem;
      opacity: 0.9;
      animation: fadeInUp 1s ease-out 0.2s both;
    }

    .cta-buttons {
      display: flex;
      gap: 1rem;
      animation: fadeInUp 1s ease-out 0.4s both;
    }

    .btn {
      padding: 1rem 2rem;
      border-radius: 10px;
      text-decoration: none;
      font-weight: 600;
      transition: all 0.3s ease;
      display: inline-block;
    }

    .btn-primary {
      background: white;
      color: #0077C2;
    }

    .btn-primary:hover {
      transform: translateY(-2px);
      box-shadow: 0 10px 30px rgba(255, 255, 255, 0.3);
    }

    .btn-secondary {
      background: transparent;
      color: white;
      border: 2px solid white;
    }

    .btn-secondary:hover {
      background: white;
      color: #0077C2;
    }

    .hero-animation {
      flex: 1;
      display: flex;
      justify-content: center;
      align-items: center;
    }

    .water-drop {
      width: 200px;
      height: 240px;
      background: rgba(255, 255, 255, 0.2);
      border-radius: 50% 50% 50% 50% / 60% 60% 40% 40%;
      animation: float 3s ease-in-out infinite;
      position: relative;
    }

    .water-drop::before {
      content: '';
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      width: 150px;
      height: 180px;
      background: rgba(255, 255, 255, 0.1);
      border-radius: 50% 50% 50% 50% / 60% 60% 40% 40%;
    }

    .features-section {
      padding: 5rem 0;
      background: #f8f9fa;
    }

    .container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 0 2rem;
    }

    .features-section h2 {
      text-align: center;
      font-size: 2.5rem;
      color: #333;
      margin-bottom: 3rem;
    }

    .features-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      gap: 2rem;
    }

    .feature-card {
      background: white;
      padding: 2rem;
      border-radius: 15px;
      text-align: center;
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
      transition: transform 0.3s ease;
    }

    .feature-card:hover {
      transform: translateY(-10px);
    }

    .feature-icon {
      font-size: 3rem;
      margin-bottom: 1rem;
    }

    .feature-card h3 {
      color: #0077C2;
      margin-bottom: 1rem;
      font-size: 1.5rem;
    }

    .feature-card p {
      color: #666;
      line-height: 1.6;
    }

    @keyframes fadeInUp {
      from {
        opacity: 0;
        transform: translateY(30px);
      }
      to {
        opacity: 1;
        transform: translateY(0);
      }
    }

    @keyframes float {
      0%, 100% { transform: translateY(0px); }
      50% { transform: translateY(-20px); }
    }

    @media (max-width: 768px) {
      .hero-section {
        flex-direction: column;
        text-align: center;
        padding: 2rem;
      }

      .hero-content h1 {
        font-size: 2.5rem;
      }

      .cta-buttons {
        flex-direction: column;
        align-items: center;
      }

      .water-drop {
        width: 150px;
        height: 180px;
      }
    }
  `]
})
export class HomeComponent {}
