import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin-login',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="login-container">
      <!-- Section d'en-tête avec navigation et message d'accès restreint -->
      <div class="admin-header-section">
        <div class="admin-access-notice">
          <div class="notice-icon">🔒</div>
          <div class="notice-content">
            <h2>Zone d'Administration IGA</h2>
            <p>Accès restreint - Réservé au personnel autorisé de l'entreprise IGA</p>
          </div>
        </div>
        
        <div class="navigation-controls">
          <button class="home-btn" (click)="goToHome()">
            <span class="btn-icon">🏠</span>
            Retour à l'accueil
          </button>
        </div>
      </div>

      <div class="login-content">
        <div class="login-card">
          <div class="login-header">
            <h2>Administration IGA</h2>
            <p>Connectez-vous pour accéder au tableau de bord</p>
          </div>
          
          <form (ngSubmit)="onLogin()" class="login-form">
            <div class="form-group">
              <label for="email">Email</label>
              <input 
                type="email" 
                id="email" 
                [(ngModel)]="credentials.email" 
                name="email"
                required 
                autocomplete="username"
              >
            </div>
            
            <div class="form-group">
              <label for="password">Mot de passe</label>
              <div class="password-field">
                <input 
                  [type]="showPassword ? 'text' : 'password'" 
                  id="password" 
                  [(ngModel)]="credentials.password" 
                  name="password"
                  required 
                  autocomplete="current-password"
                >
                <button 
                  type="button" 
                  class="password-toggle"
                  (click)="togglePassword()"
                >
                  {{ showPassword ? '🙈' : '👁️' }}
                </button>
              </div>
            </div>
            
            <div *ngIf="errorMessage" class="error-message">
              {{ errorMessage }}
            </div>
            
            <button type="submit" class="login-btn" [disabled]="isLoading">
              {{ isLoading ? 'Connexion...' : 'Se connecter' }}
            </button>
          </form>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .login-container {
      min-height: 100vh;
      display: flex;
      align-items: center;
      justify-content: center;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      padding: 1rem;
    }

    .admin-header-section {
      width: 100%;
      max-width: 800px;
      margin-bottom: 2rem;
      background: rgba(255, 255, 255, 0.9);
      border-radius: 10px;
      padding: 1.5rem;
      box-shadow: 0 10px 25px rgba(0,0,0,0.1);
    }

    .admin-access-notice {
      display: flex;
      align-items: center;
      margin-bottom: 1rem;
    }

    .notice-icon {
      font-size: 2rem;
      color: #e74c3c;
      margin-right: 0.5rem;
    }

    .notice-content h2 {
      margin: 0;
      font-size: 1.2rem;
      color: #333;
    }

    .notice-content p {
      margin: 0;
      font-size: 0.9rem;
      color: #666;
    }

    .navigation-controls {
      text-align: right;
    }

    .home-btn {
      display: inline-flex;
      align-items: center;
      background: #667eea;
      color: white;
      border: none;
      border-radius: 5px;
      padding: 0.75rem 1.5rem;
      font-size: 1rem;
      cursor: pointer;
      transition: background 0.3s ease;
    }

    .home-btn:hover {
      background: #5a6abf;
    }

    .login-content {
      width: 100%;
      max-width: 400px;
    }

    .login-card {
      background: white;
      border-radius: 10px;
      padding: 2rem;
      box-shadow: 0 10px 25px rgba(0,0,0,0.1);
    }

    .login-header {
      text-align: center;
      margin-bottom: 2rem;
    }

    .login-header h2 {
      color: #333;
      margin-bottom: 0.5rem;
    }

    .login-header p {
      color: #666;
      font-size: 0.9rem;
    }

    .form-group {
      margin-bottom: 1.5rem;
    }

    .form-group label {
      display: block;
      margin-bottom: 0.5rem;
      color: #333;
      font-weight: 500;
    }

    .form-group input {
      width: 100%;
      padding: 0.75rem;
      border: 2px solid #e1e5e9;
      border-radius: 5px;
      font-size: 1rem;
      transition: border-color 0.3s ease;
    }

    .form-group input:focus {
      outline: none;
      border-color: #667eea;
    }

    .password-field {
      position: relative;
    }

    .password-toggle {
      position: absolute;
      right: 10px;
      top: 50%;
      transform: translateY(-50%);
      background: none;
      border: none;
      color: #667eea;
      cursor: pointer;
      opacity: 0.7;
      transition: opacity 0.3s ease;
    }

    .password-toggle:hover {
      opacity: 1;
    }

    .error-message {
      color: #e74c3c;
      font-size: 0.8rem;
      margin-top: 0.5rem;
      display: block;
      text-align: center;
      background: #ffeaea;
      padding: 0.5rem;
      border-radius: 4px;
    }

    .login-btn {
      width: 100%;
      padding: 1rem;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      color: white;
      border: none;
      border-radius: 5px;
      font-size: 1rem;
      font-weight: 500;
      cursor: pointer;
      transition: transform 0.2s ease;
    }

    .login-btn:hover:not(:disabled) {
      transform: translateY(-2px);
    }

    .login-btn:disabled {
      opacity: 0.7;
      cursor: not-allowed;
      transform: none;
    }

    @media (max-width: 480px) {
      .login-card {
        padding: 1.5rem;
      }
    }
  `]
})
export class AdminLoginComponent {
  credentials = {
    email: '',
    password: ''
  };

  showPassword = false;
  isLoading = false;
  errorMessage = '';

  constructor(private router: Router) {}

  togglePassword() {
    this.showPassword = !this.showPassword;
  }

  onLogin() {
    this.isLoading = true;
    this.errorMessage = '';

    // Simulation d'une authentification
    setTimeout(() => {
      if (this.credentials.email === 'admin@iga.com' && this.credentials.password === 'admin123') {
        this.router.navigate(['/admin/dashboard']);
      } else {
        this.errorMessage = 'Email ou mot de passe incorrect';
      }
      this.isLoading = false;
    }, 1000);
  }

  goToHome() {
    this.router.navigate(['/']);
  }
}
