import { Component, OnInit, HostListener, ElementRef, ViewChild } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { RainAnimationComponent } from '../../shared/components/rain-animation/rain-animation.component';
import { PlantGrowthComponent } from '../../shared/components/plant-growth/plant-growth.component';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, RouterModule, RainAnimationComponent, PlantGrowthComponent],
  template: `
    <div class="home-container">
      <!-- Section Hero avec animation pluie -->
      <section class="hero-section" #heroSection>
        <app-rain-animation></app-rain-animation>
        
        <div class="hero-content">
          <div class="container">
            <div class="hero-text" [class.visible]="heroVisible">
              <h1 class="hero-title">
                <span class="gradient-text">Groupe IGA</span>
                <br>
                <span class="subtitle">Experts en Hydrogéologie</span>
              </h1>
              <p class="hero-description">
                Nous sommes spécialisés dans le conseil hydrogéologique, 
                les études de forages et les prestations techniques liées à l'eau souterraine.
              </p>
              <div class="hero-actions">
                <button class="btn btn-primary" routerLink="/services">
                  Nos Services
                </button>
                <button class="btn btn-outline" routerLink="/contact">
                  Nous Contacter
                </button>
              </div>
            </div>
            
            <div class="hero-visual">
              <div class="water-drop-large floating">
