      location: 'Kara, Togo',
      image: '/assets/images/project3.jpg'
    }
  ];

  ngOnInit() {
    // Animation d'entrée du hero
    setTimeout(() => {
      this.heroVisible = true;
    }, 500);

    this.setupScrollAnimations();
  }

  @HostListener('window:scroll', ['$event'])
  onScroll() {
    this.checkScrollAnimations();
  }

  private setupScrollAnimations() {
    // Configuration des observers pour les animations au scroll
    const options = {
      threshold: 0.3,
      rootMargin: '0px 0px -50px 0px'
    };

    // Observer pour les services
    const servicesObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          this.servicesVisible = true;
        }
      });
    }, options);

    // Observer pour les projets
    const projectsObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          this.projectsVisible = true;
        }
      });
    }, options);

    // Observer les sections quand elles sont disponibles
    setTimeout(() => {
      const servicesSection = document.querySelector('.services-preview');
      const projectsSection = document.querySelector('.recent-projects');

      if (servicesSection) servicesObserver.observe(servicesSection);
      if (projectsSection) projectsObserver.observe(projectsSection);
    }, 100);
  }

  private checkScrollAnimations() {
    // Logique supplémentaire pour les animations au scroll si nécessaire
  }
}
