import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-navbar',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <nav class="navbar" [class.scrolled]="isScrolled">
      <div class="container">
        <div class="navbar-brand">
          <img src="/assets/images/logo-iga.png" alt="IGA Logo" class="logo">
          <span class="brand-text">Groupe IGA</span>
        </div>
        
        <ul class="navbar-nav" [class.mobile-open]="mobileMenuOpen">
          <li><a routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">Accueil</a></li>
          <li><a routerLink="/about" routerLinkActive="active">À Propos</a></li>
          <li><a routerLink="/services" routerLinkActive="active">Services</a></li>
          <li><a routerLink="/projects" routerLinkActive="active">Projets</a></li>
          <li><a routerLink="/partners" routerLinkActive="active">Partenaires</a></li>
          <li><a routerLink="/contact" routerLinkActive="active">Contact</a></li>
          <li><a routerLink="/admin" class="admin-link">Admin</a></li>
        </ul>
        
        <button class="mobile-toggle" (click)="toggleMobileMenu()" [class.active]="mobileMenuOpen">
          <span></span>
          <span></span>
          <span></span>
        </button>
      </div>
    </nav>
  `,
  styles: [`
    .navbar {
      position: fixed;
      top: 0;
      left: 0;
      right: 0;
      z-index: 1000;
      background: rgba(255, 255, 255, 0.95);
      backdrop-filter: blur(10px);
      transition: all 0.3s ease;
      border-bottom: 1px solid transparent;
    }

    .navbar.scrolled {
      background: rgba(255, 255, 255, 0.98);
      border-bottom-color: rgba(0, 119, 194, 0.1);
      box-shadow: 0 2px 20px rgba(0, 0, 0, 0.1);
    }

    .container {
      display: flex;
      align-items: center;
      justify-content: space-between;
      padding: 1rem 2rem;
      max-width: 1200px;
      margin: 0 auto;
    }

    .navbar-brand {
      display: flex;
      align-items: center;
      gap: 0.75rem;
    }

    .logo {
      height: 40px;
      width: auto;
    }

    .brand-text {
      font-size: 1.5rem;
      font-weight: 700;
      background: var(--gradient-water);
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      background-clip: text;
    }

    .navbar-nav {
      display: flex;
      list-style: none;
      margin: 0;
      padding: 0;
      gap: 2rem;
    }

    .navbar-nav a {
      text-decoration: none;
      color: var(--text-dark);
      font-weight: 500;
      padding: 0.5rem 1rem;
      border-radius: 25px;
      transition: all 0.3s ease;
      position: relative;
    }

    .navbar-nav a:hover {
      color: var(--primary-blue);
      background: rgba(0, 119, 194, 0.1);
    }

    .navbar-nav a.active {
      color: var(--primary-blue);
      background: rgba(0, 119, 194, 0.15);
    }

    .admin-link {
      background: var(--gradient-water) !important;
      color: white !important;
    }

    .admin-link:hover {
      transform: translateY(-2px);
      box-shadow: 0 5px 15px rgba(0, 119, 194, 0.3);
    }

    .mobile-toggle {
      display: none;
      flex-direction: column;
      background: none;
      border: none;
      cursor: pointer;
      padding: 0.5rem;
    }

    .mobile-toggle span {
      width: 25px;
      height: 3px;
      background: var(--primary-blue);
      margin: 3px 0;
      transition: 0.3s;
      border-radius: 2px;
    }

    .mobile-toggle.active span:nth-child(1) {
      transform: rotate(-45deg) translate(-5px, 6px);
    }

    .mobile-toggle.active span:nth-child(2) {
      opacity: 0;
    }

    .mobile-toggle.active span:nth-child(3) {
      transform: rotate(45deg) translate(-5px, -6px);
    }

    @media (max-width: 768px) {
      .navbar-nav {
        position: fixed;
        top: 100%;
        left: 0;
        right: 0;
        background: white;
        flex-direction: column;
        padding: 2rem;
        box-shadow: 0 5px 20px rgba(0, 0, 0, 0.1);
        transform: translateY(-100%);
        opacity: 0;
        visibility: hidden;
        transition: all 0.3s ease;
      }

      .navbar-nav.mobile-open {
        transform: translateY(0);
        opacity: 1;
        visibility: visible;
      }

      .mobile-toggle {
        display: flex;
      }
    }
  `]
})
export class NavbarComponent implements OnInit {
  isScrolled = false;
  mobileMenuOpen = false;

  ngOnInit() {
    window.addEventListener('scroll', () => {
      this.isScrolled = window.scrollY > 50;
    });
  }

  toggleMobileMenu() {
    this.mobileMenuOpen = !this.mobileMenuOpen;
  }
}
