import { Component, OnInit, HostListener } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <header [class.scrolled]="isScrolled" class="main-header">
      <div class="container">
        <div class="logo-section">
          <img src="assets/logo.jpg" alt="Logo Groupe IGA" class="logo" />
          <div class="brand-text">
            <div class="brand-name">IGA</div>
            <div class="brand-tagline">L'eau en confiance</div>
          </div>
        </div>
        
        <!-- Navigation simplifiée -->
        <nav>
          <a routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">Accueil</a>
          <a href="#expertises" (click)="scrollToSection('expertises', $event)">Nos expertises</a>
          <a href="#realisations" (click)="scrollToSection('realisations', $event)">Réalisations</a>
          <a href="#a-propos" (click)="scrollToSection('a-propos', $event)">À propos</a>
          <a href="#contact" (click)="scrollToSection('contact', $event)">Contact</a>
          <a routerLink="/admin/login" routerLinkActive="active">Administration</a>
        </nav>
      </div>
    </header>
  `,
  styles: [`
    /* Header fixe avec z-index réduit pour ne pas cacher la scrollbar */
    .main-header {
      position: fixed;
      top: 0;
      left: 0;
      right: 0;
      height: 70px;
      background: rgba(255, 255, 255, 0.95);
      backdrop-filter: blur(20px);
      border-bottom: 1px solid var(--gray-200);
      z-index: 100; /* Réduit de 1000 à 100 pour ne pas cacher la scrollbar */
      transition: var(--transition);
      margin: 0;
      padding: 0;
    }

    .main-header.scrolled {
      background: rgba(255, 255, 255, 0.98);
      box-shadow: var(--shadow-md);
      border-bottom-color: var(--gray-300);
    }

    .container {
      height: 100%;
      display: flex;
      align-items: center;
      justify-content: space-between;
      max-width: 1280px;
      margin: 0 auto;
      padding: 0 2rem;
    }

    .logo-section {
      display: flex;
      align-items: center;
      gap: 1rem;
    }

    .logo {
      height: 50px;
      width: auto;
    }

    .brand-name {
      font-family: var(--font-display);
      font-size: 1.5rem;
      font-weight: 700;
      color: var(--primary);
      line-height: 1;
      margin: 0;
    }

    .brand-tagline {
      font-size: 0.75rem;
      color: var(--gray-600);
      line-height: 1;
      margin: 0;
    }

    /* Navigation simple et épurée */
    nav {
      display: flex;
      align-items: center;
      gap: 2rem;
    }

    nav a {
      text-decoration: none;
      color: var(--gray-700);
      font-weight: 500;
      font-size: 0.95rem;
      transition: color 0.3s ease;
    }

    nav a:hover,
    nav a.active {
      color: var(--primary);
    }

    /* Responsive simple */
    @media (max-width: 768px) {
      .main-header {
        height: 60px;
      }

      .container {
        padding: 0 1rem;
      }

      nav {
        gap: 1rem;
      }

      nav a {
        font-size: 0.9rem;
      }

      .logo {
        height: 40px;
      }

      .brand-name {
        font-size: 1.25rem;
      }

      .brand-tagline {
        font-size: 0.7rem;
      }
    }

    @media (max-width: 480px) {
      .main-header {
        height: 50px;
      }

      nav {
        gap: 0.5rem;
      }

      nav a {
        font-size: 0.8rem;
      }

      .logo {
        height: 35px;
      }

      .brand-name {
        font-size: 1.1rem;
      }
    }
  `]
})
export class HeaderComponent implements OnInit {
  isScrolled = false;

  ngOnInit() {
    // Initialisation simple
  }

  @HostListener('window:scroll', [])
  onWindowScroll() {
    this.isScrolled = window.pageYOffset > 50;
  }

  scrollToSection(sectionId: string, event: Event) {
    event.preventDefault();

    const targetSection = document.getElementById(sectionId);
    if (targetSection) {
      const headerHeight = this.getHeaderHeight();
      const targetPosition = targetSection.offsetTop - headerHeight;

      window.scrollTo({
        top: targetPosition,
        behavior: 'smooth'
      });
    }
  }

  private getHeaderHeight(): number {
    if (window.innerWidth <= 480) return 50;
    if (window.innerWidth <= 768) return 60;
    return 70;
  }
}
