import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-services',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <div class="services-container">
      <!-- Hero Section -->
      <section class="services-hero">
        <div class="container">
          <div class="hero-content">
            <h1 class="hero-title">Nos Services d'Expertise</h1>
            <p class="hero-description">
              Découvrez nos solutions complètes en hydrogéologie et géotechnique
            </p>
            <div class="water-flow-animation">
              <svg viewBox="0 0 100 20" class="flow-svg">
                <path class="flow-path" d="M0,10 Q25,5 50,10 T100,10" stroke="#0077C2" stroke-width="2" fill="none"/>
                <circle class="flow-dot" r="3" fill="#0077C2">
                  <animateMotion dur="3s" repeatCount="indefinite">
                    <mpath href="#flow"/>
                  </animateMotion>
                </circle>
                <defs>
                  <path id="flow" d="M0,10 Q25,5 50,10 T100,10"/>
                </defs>
              </svg>
            </div>
          </div>
        </div>
      </section>

      <!-- Services Grid -->
      <section class="services-grid-section">
        <div class="container">
          <div class="services-grid">
            <div class="service-card premium" 
                 *ngFor="let service of services; let i = index"
                 [style.animation-delay]="(i * 100) + 'ms'">
              <div class="service-header">
                <div class="service-icon">
                  <i [class]="service.icon"></i>
                </div>
                <h3>{{ service.title }}</h3>
              </div>
              
              <div class="service-content">
                <p>{{ service.description }}</p>
                
                <ul class="service-features">
                  <li *ngFor="let feature of service.features">
                    <i class="fas fa-check"></i>
                    {{ feature }}
                  </li>
                </ul>
              </div>
              
              <div class="service-footer">
                <button class="btn btn-outline-primary">
                  En savoir plus
                </button>
              </div>
              
              <!-- Effet de vague animée -->
              <div class="wave-effect">
                <svg viewBox="0 0 200 50" class="wave">
                  <path class="wave-path" 
                        d="M0,25 Q50,10 100,25 T200,25 V50 H0 Z" 
                        fill="url(#waveGradient)"/>
                  <defs>
                    <linearGradient id="waveGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                      <stop offset="0%" style="stop-color:#0077C2;stop-opacity:0.1"/>
                      <stop offset="100%" style="stop-color:#4CAF50;stop-opacity:0.3"/>
                    </linearGradient>
                  </defs>
                </svg>
              </div>
            </div>
          </div>
        </div>
      </section>

      <!-- Process Section -->
      <section class="process-section">
        <div class="container">
          <h2 class="section-title">Notre Méthodologie</h2>
          
          <div class="process-timeline">
            <div class="process-step" 
                 *ngFor="let step of processSteps; let i = index"
                 [class.active]="i <= currentStep">
              <div class="step-number">{{ i + 1 }}</div>
              <div class="step-content">
                <h4>{{ step.title }}</h4>
                <p>{{ step.description }}</p>
              </div>
              <div class="step-connector" *ngIf="i < processSteps.length - 1">
                <div class="connector-line"></div>
                <div class="water-drop">
                  <i class="fas fa-tint"></i>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      <!-- CTA Section -->
      <section class="services-cta">
        <div class="container">
          <div class="cta-content">
