import { Component, OnInit, HostListener } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
  isScrolled = false;

  ngOnInit() {
    // Initialisation simple
  }

  @HostListener('window:scroll', [])
  onWindowScroll() {
    this.isScrolled = window.pageYOffset > 50;
  }

  scrollToSection(sectionId: string, event: Event) {
    event.preventDefault();

    const targetSection = document.getElementById(sectionId);
    if (targetSection) {
      const headerHeight = this.getHeaderHeight();
      const targetPosition = targetSection.offsetTop - headerHeight;

      window.scrollTo({
        top: targetPosition,
        behavior: 'smooth'
      });
    }
  }

  private getHeaderHeight(): number {
    if (window.innerWidth <= 480) return 50;
    if (window.innerWidth <= 768) return 60;
    return 70;
  }
}
<header [class.scrolled]="isScrolled" class="main-header">
  <div class="container">
    <div class="logo-section">
      <img src="assets/logo.jpg" alt="Logo Groupe IGA" class="logo" />
      <div class="brand-text">
        <div class="brand-name">IGA</div>
        <div class="brand-tagline">L'eau en confiance</div>
      </div>
    </div>

    <!-- Navigation simple -->
    <nav>
      <a routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">Accueil</a>
      <a href="#expertises" (click)="scrollToSection('expertises', $event)">Nos expertises</a>
      <a href="#realisations" (click)="scrollToSection('realisations', $event)">Réalisations</a>
      <a href="#a-propos" (click)="scrollToSection('a-propos', $event)">À propos</a>
      <a href="#contact" (click)="scrollToSection('contact', $event)">Contact</a>
      <a routerLink="/admin/login" routerLinkActive="active">Administration</a>
    </nav>
  </div>
</header>
